import { SIGN_IN, SIGN_OUT } from "./types";

// action creator - return an object with type and payload(optional)
export const signIn = (userId) => {
  return {
    type: SIGN_IN,
    payload: userId,
  };
};

export const signOut = () => {
  return {
    type: SIGN_OUT,
  };
};
